mapper.select:
select *
from user_tb
order by userNum desc limit #{startRow}, #{pageSize}

select *
from user_tb
where userId = #{userId}

select UNREGIST
from user_tb
where userId = #{userId}

select *
from project_tb
where ID = #{ID}

select PROSTATUS
from project_tb
where IDX = #{IDX}

select * 
from payment_tb
where PSTATUS = 'PAY00';

select *
from payment_tb
where PSTATUS = 'PAY01';

select *
from payment_tb
where PSTATUS = 'PAY02';

select * 
from notice_tb
order by IDX desc limit #{startRow}, #{pageSize}

select *
from notice_tb
where IDX = #{IDX}

SELECT ID, EVENT_ALR
  FROM USER_TB
 WHERE EVENT_ALR = 'Y';
