mapper.select:
select *
from user_tb
order by userNum desc limit #{startRow}, #{pageSize}

select *
from user_tb
where userId = #{userId}

select UNREGIST
from user_tb
where userId = #{userId}

select *
from project_tb
where ID = #{ID}

select STATUS
from project_tb
where IDX = #{IDX}

mapper.update:
update project_tb  
set STATUS = 'PJT01'

where IDX = #{IDX}

update project_tb
set STATUS = 'PJT00'

where IDX = #{IDX}

mapper.select:
