mapper.insert:
	 	INSERT INTO ADDRESS_TB(IDX
	 			  			 ,ID
	 			  			 ,ADDRESS_NM
	 			  			 ,ADDRESS
	 			  			 ,NAME
	 			  			 ,PHONE
	 			  			 ,ZIP_CODE)
			 VALUES (	
	    			  (SELECT IDX
             		     FROM ( SELECT IFNULL(MAX(CAST(IDX AS UNSIGNED)), 0) + 1 AS IDX
                    				  FROM ADDRESS_TB
                				   ) A)
	    			  ,#{id}
	   			      ,#{addressNm}
	   			      ,#{address}
	    			  ,#{name}
	     			  ,#{phone}
	 			      ,#{zipCode})
