mapper.insert:
           INSERT INTO PROJECT_TB (
                      IDX   
                    , ID 

                    , CRE_NM


                    , CRE_PRO


                    , CRE_INTRO 

                    , CATEGORY 

                    , TITLE 


                    , PRODUCT_NM


                    , IMG1  


                    , INTRO  


                    , BUDGET


                    , SCHEDULE  


                    , MIN_DONA    


                    , TARGET_AMT  


                    , START


                    , END  

                    , STATUS
                  ) 
              VALUES ( 
              		   IFNULL( (SELECT IDX
						      FROM (
						      		SELECT IDX 
						      		  FROM PROJECT_TB
						             WHERE IDX = #{idx}) A )
						 , (SELECT IDX
				              FROM ( 
				             		SELECT IFNULL(MAX(CAST(IDX AS UNSIGNED)), 0) + 1 AS IDX
   				 			  		  FROM PROJECT_TB) B )
            		  )
                 	  , #{id}

                     , #{creNm}  


                     , #{crePro}


                     , #{creIntro}

                     , (SELECT CATEGORY_CD
                     	   FROM (SELECT CATEGORY_CD
 								   FROM CATEGORY_TB
                                 WHERE CATEGORY_NM = #{category}) CATEGORY)

                     , #{title}


                     , #{productNm}


				      , #{img1}


                     , #{intro}  


                     , #{budget} 


                     , #{schedule}


                     , REPLACE(#{minDona}, ',', '')


                     , REPLACE(#{targetAmt}, ',', '')


                     , #{start}


                     , #{end}

                     , #{status}
                     )
             ON DUPLICATE KEY
		  UPDATE ID = #{id}

		  	   , CRE_NM 	 = #{creNm}


			   , CRE_PRO     = #{crePro}


			   , CRE_INTRO   = #{creIntro}

			   , CATEGORY    = (SELECT CATEGORY_CD
                     	  			  FROM (SELECT CATEGORY_CD
 								              FROM CATEGORY_TB
                                            WHERE CATEGORY_NM = #{category}) CATEGORY)

		       , TITLE       = #{title}


			   , PRODUCT_NM  = #{productNm}


			   , IMG1        = #{img1} 


			   , INTRO       = #{intro}


			   , BUDGET      = #{budget} 


			   , SCHEDULE    = #{schedule}


			   , MIN_DONA    = REPLACE(#{minDona}, ',' , '')


			   , TARGET_AMT  = REPLACE(#{targetAmt}, ',' , '')


			   , START        = #{start}


			   , END         = #{end}

			   , STATUS		 = #{status}
