mapper.insert:
	INSERT INTO PAYMENT_TB
	 	  VALUE (
	 	      (SELECT IDX
                  FROM (
                      SELECT IFNULL(MAX(CAST(IDX AS UNSIGNED)), 0) + 1 AS IDX
                       FROM payment_tb
                   ) A)
	 	      ,#{pjIdx}
	 	      ,#{id}
	 	      ,#{amount}
	 	      ,#{address}
	 	      ,#{phone}
	 	      ,#{date}
	 	      ,#{payDate}
	 	      ,#{status}
	 	      )
