mapper.select:
	SELECT COUNT(*)  AS CNT
      FROM LIKE_TB
     WHERE PJ_IDX = #{PJ_IDX}
       AND USER_ID = #{USER_ID}

mapper.delete:
		DELETE FROM LIKE_TB
    	 WHERE PJ_IDX = #{PJ_IDX}
      	   AND USER_ID = #{USER_ID}

mapper.select:
	SELECT COUNT(*)  AS CNT
      FROM ALRAM_TB
     WHERE PJ_IDX = #{PJ_IDX}
       AND USER_ID = #{USER_ID}

mapper.insert:
INSERT INTO ALRAM_TB ( IDX , PJ_IDX , USER_ID)
VALUES ( (SELECT NUM FROM (SELECT IFNULL(MAX(IDX), 0) + 1 AS NUM FROM ALRAM_TB) A) , #{PJ_IDX} , #{USER_ID}
)

mapper.delete:
	DELETE FROM ALRAM_TB
     WHERE PJ_IDX = #{PJ_IDX}
       AND USER_ID = #{USER_ID}
