mapper.select:
	SELECT COUNT(*)  AS CNT
      FROM LIKE_TB
     WHERE PJ_IDX = #{PJ_IDX}
       AND USER_ID = #{USER_ID}

mapper.delete:
	DELETE FROM LIKE_TB
     WHERE PJ_IDX = #{PJ_IDX}
       AND USER_ID = #{USER_ID}

mapper.select:
	SELECT SUM(AMOUNT) AS sumMoney
  	  FROM PAYMENT_TB
  	 WHERE PJ_IDX = #{IDX}

SELECT COUNT(ID) AS sumUser
  FROM PAYMENT_TB
 where PJ_IDX = #{IDX};
